\documentclass{article}
\usepackage{tikz}
\usepackage{caption}
\usetikzlibrary{shapes,arrows,decorations.pathreplacing}

\begin{document}

\pagenumbering{gobble}
\tikzstyle{block} = [rectangle, draw, fill=white, text width=18em, text centered, rounded corners, minimum height=3.5em]
\tikzstyle{line} = [draw, -latex']
\tikzstyle{decision} = [diamond, draw, fill=white, text width=6em, text badly centered, node distance=3.3cm, inner sep=0pt]

\begin{figure}
    \centering
    \vspace*{-4cm}
    \begin{tikzpicture}[node distance = 2cm, auto]

    % Node with text
    \node (step100) [block] at (0,0) {Providing a compact form factor chassis for easy transportation};
    \node (step101) [block, below of=step100] {Integrating a battery power source for untethered use within the chassis};
    \node (step102) [block, below of=step101] {Incorporating various input/output ports for connectivity within the chassis};
    \node (step103) [block, below of=step102] {Assembling integrated keyboard and display within the chassis to form portable computing device};

    % Arrows and labels
    \path [line] (step100) -- (step101);
    \path [line] (step101) -- (step102);
    \path [line] (step102) -- (step103);

    % Arcs and labels
    \draw[-, postaction={decorate}] (step100.east) arc (150:125:2cm);
    \path (step100.east) ++(25:1.5cm) node[anchor=east] {100};

    \draw[-, postaction={decorate}] (step101.east) arc (150:125:2cm);
    \path (step101.east) ++(25:1.5cm) node[anchor=east] {101};

    \draw[-, postaction={decorate}] (step102.east) arc (150:125:2cm);
    \path (step102.east) ++(25:1.5cm) node[anchor=east] {102};

    \draw[-, postaction={decorate}] (step103.east) arc (150:125:2cm);
    \path (step103.east) ++(25:1.5cm) node[anchor=east] {103};

    \end{tikzpicture}
    \caption*{\textbf{Fig. 1}}
    \label{fig:arcexample}
\end{figure}

\end{document}
